#!/usr/bin/env python3\n\"\"\"\nConfiguration management for MT5 Real-Time Analytics Platform\nEnvironment variables, database settings, and application config\n\"\"\"\n\nimport os\nfrom typing import List\nfrom pydantic_settings import BaseSettings\n\nclass Settings(BaseSettings):\n    \"\"\"\n    Application settings loaded from environment variables\n    \"\"\"\n    \n    # Application\n    APP_NAME: str = \"MT5 Real-Time Analytics Platform\"\n    VERSION: str = \"1.0.0\"\n    DEBUG: bool = True\n    ENVIRONMENT: str = \"development\"\n    \n    # Database\n    DATABASE_URL: str = \"sqlite:///./data/mt5_analytics.db\"\n    \n    # Redis\n    REDIS_URL: str = \"redis://localhost:6379\"\n    \n    # Security\n    SECRET_KEY: str = \"your-super-secret-key-change-in-production\"\n    JWT_SECRET_KEY: str = \"your-jwt-secret-key\"\n    ALGORITHM: str = \"HS256\"\n    ACCESS_TOKEN_EXPIRE_MINUTES: int = 30\n    \n    # CORS\n    ALLOWED_ORIGINS: List[str] = [\"http://localhost:3000\", \"http://localhost:8000\"]\n    \n    # MT5 Configuration\n    MT5_SYMBOLS: str = \"GOLD,EURUSD,GBPUSD\"\n    MT5_SERVER: str = \"Demo-Server\"\n    MT5_LOGIN: int = 0\n    MT5_PASSWORD: str = \"\"\n    MT5_TIMEOUT: int = 10000\n    \n    # Analytics\n    ROLLING_WINDOW_HOURS: int = 168  # 1 week\n    MIN_SAMPLE_SIZE: int = 25\n    CONFIDENCE_LEVEL: float = 0.95\n    UPDATE_INTERVAL_SECONDS: int = 10\n    \n    # Alerts\n    TELEGRAM_BOT_TOKEN: str = \"\"\n    TELEGRAM_CHAT_ID: str = \"\"\n    EMAIL_USERNAME: str = \"\"\n    EMAIL_PASSWORD: str = \"\"\n    SMTP_SERVER: str = \"smtp.gmail.com\"\n    SMTP_PORT: int = 587\n    \n    # Rate Limiting\n    RATE_LIMIT_REQUESTS: int = 100\n    RATE_LIMIT_WINDOW: int = 3600  # 1 hour\n    \n    class Config:\n        env_file = \".env\"\n        case_sensitive = True\n\n# Global settings instance\nsettings = Settings()\n\n# Derived settings\nSQLITE_DATABASE = settings.DATABASE_URL.replace(\"sqlite:///\", \"\")\nMT5_SYMBOLS_LIST = [s.strip() for s in settings.MT5_SYMBOLS.split(\",\")]